import React, { useState, useEffect } from 'react';
import { useLocation, useParams, useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const TokenDetail = () => {
  const { tokenAddress } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { card } = location.state || {};

  const [owners, setOwners] = useState([]);
  const [transfers, setTransfers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalSupply, setTotalSupply] = useState('0');
  const [remainingTokens, setRemainingTokens] = useState('0');
  const [purchaseAmount, setPurchaseAmount] = useState('');
  const [cost, setCost] = useState('0');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [dogeMoveX, setDogeMoveX] = useState(0);
  const [dogeMoveY, setDogeMoveY] = useState(0);

  const tokenDetails = card || {
    name: 'Unknown Token',
    symbol: 'UNKNOWN',
    description: 'No description available',
    tokenImageUrl: '/api/placeholder/200/200',
    fundingRaised: '0 ETH',
    creatorAddress: '0x0000000000000000000000000000000000000000',
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX - window.innerWidth / 2) / 50;
      const y = (e.clientY - window.innerHeight / 2) / 50;
      setDogeMoveX(x);
      setDogeMoveY(y);
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const chartData = {
    labels: owners.map(owner => `${owner.owner_address.slice(0, 6)}...${owner.owner_address.slice(-4)}`),
    datasets: [{
      data: owners.map(owner => parseFloat(owner.percentage_relative_to_total_supply)),
      backgroundColor: [
        'rgba(255, 206, 86, 0.8)',
        'rgba(255, 99, 132, 0.8)',
        'rgba(54, 162, 235, 0.8)',
        'rgba(75, 192, 192, 0.8)',
        'rgba(153, 102, 255, 0.8)',
      ],
      borderWidth: 1
    }]
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-yellow-300 to-yellow-400 text-gray-800">
      <nav className="sticky top-0 z-50 bg-orange-400 shadow-lg">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <button
              onClick={() => navigate(-1)}
              className="flex items-center gap-2 text-white hover:text-yellow-200 transition-colors font-bold"
            >
              ‚Üê Back
            </button>
            <div className="text-2xl font-extrabold text-white">
              {tokenDetails.name} Details
            </div>
          </div>
        </div>
      </nav>

      <div className="container mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Left Column - Token Info */}
          <div className="bg-white rounded-3xl p-8 shadow-xl transform hover:rotate-1 transition-transform">
            <div className="relative">
              <div 
                className="absolute -top-12 right-0 text-8xl select-none"
                style={{
                  transform: `translate(${dogeMoveX}px, ${dogeMoveY}px) rotate(${dogeMoveX}deg)`
                }}
              >
                ü™ô
              </div>
              <img 
                src={tokenDetails.tokenImageUrl} 
                alt={tokenDetails.name}
                className="w-48 h-48 mx-auto mb-6 rounded-xl shadow-lg"
              />
            </div>

            <div className="space-y-4">
              <div className="bg-yellow-50 rounded-xl p-4">
                <h3 className="text-xl font-bold text-purple-600 mb-2">Token Info</h3>
                <p className="text-gray-600"><span className="font-bold">Symbol:</span> {tokenDetails.symbol}</p>
                <p className="text-gray-600"><span className="font-bold">Description:</span> {tokenDetails.description}</p>
              </div>

              <div className="bg-yellow-50 rounded-xl p-4">
                <h3 className="text-xl font-bold text-purple-600 mb-2">Contract Details</h3>
                <p className="text-gray-600 break-all"><span className="font-bold">Address:</span> {tokenAddress}</p>
                <p className="text-gray-600 break-all"><span className="font-bold">Creator:</span> {tokenDetails.creatorAddress}</p>
              </div>
            </div>
          </div>

          {/* Right Column - Stats & Purchase */}
          <div className="space-y-8">
            {/* Stats Cards */}
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-white rounded-3xl p-6 shadow-xl transform hover:-rotate-2 transition-transform">
                <div className="text-4xl mb-2">üíé</div>
                <h3 className="text-lg font-bold text-purple-600">Total Supply</h3>
                <p className="text-2xl font-bold">{totalSupply}</p>
              </div>

              <div className="bg-white rounded-3xl p-6 shadow-xl transform hover:rotate-2 transition-transform">
                <div className="text-4xl mb-2">üöÄ</div>
                <h3 className="text-lg font-bold text-purple-600">Funding Raised</h3>
                <p className="text-2xl font-bold">{tokenDetails.fundingRaised}</p>
              </div>
            </div>

            {/* Purchase Card */}
            <div className="bg-white rounded-3xl p-8 shadow-xl transform hover:scale-105 transition-transform">
              <h3 className="text-2xl font-bold text-purple-600 mb-6">Buy Tokens</h3>
              <div className="space-y-4">
                <input
                  type="number"
                  placeholder="Amount of tokens to buy"
                  value={purchaseAmount}
                  onChange={(e) => setPurchaseAmount(e.target.value)}
                  className="w-full px-6 py-4 rounded-xl border-2 border-yellow-300 focus:border-purple-500 focus:ring-2 focus:ring-purple-500 outline-none transition-all"
                />
                <button 
                  onClick={() => setIsModalOpen(true)}
                  className="w-full py-4 bg-purple-600 text-white rounded-xl font-bold text-lg transform hover:scale-105 transition-all hover:bg-purple-500"
                >
                  Purchase Tokens üöÄ
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Charts Section */}
        <div className="mt-12 grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white rounded-3xl p-8 shadow-xl">
            <h3 className="text-2xl font-bold text-purple-600 mb-6">Ownership Distribution</h3>
            <div className="h-[300px]">
              <Pie data={chartData} />
            </div>
          </div>

          <div className="bg-white rounded-3xl p-8 shadow-xl">
            <h3 className="text-2xl font-bold text-purple-600 mb-6">Recent Transfers</h3>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b-2 border-yellow-200">
                    <th className="px-4 py-2 text-left">From</th>
                    <th className="px-4 py-2 text-left">To</th>
                    <th className="px-4 py-2 text-right">Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {transfers.slice(0, 5).map((transfer, index) => (
                    <tr key={index} className="border-b border-yellow-100">
                      <td className="px-4 py-2 font-mono text-sm">
                        {transfer.from_address.slice(0, 6)}...{transfer.from_address.slice(-4)}
                      </td>
                      <td className="px-4 py-2 font-mono text-sm">
                        {transfer.to_address.slice(0, 6)}...{transfer.to_address.slice(-4)}
                      </td>
                      <td className="px-4 py-2 text-right">
                        {ethers.formatUnits(transfer.value, 'ether')}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      {/* Purchase Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-3xl p-8 shadow-2xl max-w-md w-full transform hover:rotate-1 transition-transform">
            <h4 className="text-2xl font-bold text-purple-600 mb-6">Confirm Purchase</h4>
            <p className="text-xl mb-6">
              Cost: <span className="font-bold">{cost} ETH</span>
            </p>
            <div className="flex gap-4">
              <button 
                onClick={() => setIsModalOpen(false)}
                className="flex-1 py-3 rounded-xl font-bold bg-gray-200 hover:bg-gray-300 transition-colors"
              >
                Cancel
              </button>
              <button 
                onClick={() => {
                  // Handle purchase logic here
                  setIsModalOpen(false);
                }}
                className="flex-1 py-3 rounded-xl font-bold bg-purple-600 text-white hover:bg-purple-500 transition-colors"
              >
                Confirm üöÄ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TokenDetail;
